#123; stopTextSelectionMode(); final int textLengthInChars = text.length(); for (int i = 0; i &lt; textLengthInChars; i++) &#123; char firstChar = text.charAt(i); int codePoint; if (Character.isHighSurrogate(firstChar)) &#123; if (++i &lt; textLengthInChars) &#123; codePoint = Character.toCodePoint(firstChar, text.charAt(i)); &#125; else &#123; // At end of string, with no low surrogate following the high: codePoint = TerminalEmulator.UNICODE_REPLACEMENT_CHAR; &#125; &#125; else &#123; codePoint = firstChar; &#125; boolean ctrlHeld = false; if (codePoint &lt;= 31 &amp;&amp; codePoint != 27) &#123; if (codePoint == &#x27;
&#x27;) &#123; // The AOSP keyboard and descendants seems to send 
