pendEscape, int codePoint) &#123; if (codePoint &gt; 1114111 || (codePoint &gt;= 0xD800 &amp;&amp; codePoint &lt;= 0xDFFF)) &#123; // 1114111 (= 2**16 + 1024**2 - 1) is the highest code point, [0xD800,0xDFFF] is the surrogate range. throw new IllegalArgumentException(&quot;Invalid code point: &quot; + codePoint); &#125; int bufferPosition = 0; if (prependEscape) mUtf8InputBuffer[bufferPosition++] = 27; if (codePoint &lt;= /* 7 bits */0b1111111) &#123; mUtf8InputBuffer[bufferPosition++] = (byte) codePoint; &#125; else if (codePoint &lt;= /* 11 bits */0b11111111111) &#123; /* 110xxxxx leading byte with leading 5 bits */ mUtf8InputBuffer[bufferPosition++] = (byte) (0b11000000 | (codePoint &gt;&gt; 6)); /* 10xxxxxx continuation byte with following 6 bits */ mUtf8InputBuffer[bufferPosition++] = (byte) (0b10000000 | (codePoint &amp; 0b111111)); &#125; else if (codePoint &lt;= /* 16 bits */0b1111111111111111) &#123; /* 1110xxxx leading byte with leading 4 bits */ mUtf8InputBuffer[bufferPosition++] = (byte) (0b11100000 | (codePoint &gt;&gt; 12)); /* 10xxxxxx continuation byte with following 6 bits */ mUtf8InputBuffer[bufferPosition++] = (byte) (0b10000000 | ((codePoint &gt;&gt; 6) &amp; 0b111111)); /* 10xxxxxx continuation byte with following 6 bits */ mUtf8InputBuffer[bufferPosition++] = (byte) (0b10000000 | (codePoint &amp; 0b111111)); &#125; else &#123; /* We have checked codePoint &lt;= 1114111 above, so we have max 21 bits = 0b111111111111111111111 */ /* 11110xxx leading byte with leading 3 bits */ mUtf8InputBuffer[bufferPosition++] = (byte) (0b11110000 | (codePoint &gt;&gt; 18)); /* 10xxxxxx continuation byte with following 6 bits */ mUtf8InputBuffer[bufferPosition++] = (byte) (0b10000000 | ((codePoint &gt;&gt; 12) &amp; 0b111111)); /* 10xxxxxx continuation byte with following 6 bits */ mUtf8InputBuffer[bufferPosition++] = (byte) (0b10000000 | ((codePoint &gt;&gt; 6) &amp; 0b111111)); /* 10xxxxxx continuation byte with following 6 bits */ mUtf8InputBuffer[bufferPosition++] = (byte) (0b10000000 | (codePoint &amp; 0b111111)); &#125; write(mUtf8InputBuffer, 0, bufferPosition); &#125;&#125; 最终会传入mTerminalToProcessIOQueue来处理相关事务，它是一个byte队列，用来将terminal数据传入我们进程的数据结构，然后我们的读取线程在这里 1234567891011121314151617181920212223242526public void initializeEmulator(int columns, int rows) &#123; mEmulator = new TerminalEmulator(this, columns, rows, /* transcript= */2000); int[] processId = new int[1]; mTerminalFileDescriptor = JNI.createSubprocess(mShellPath, mCwd, mArgs, mEnv, processId, rows, columns); mShellPid = processId[0]; final FileDescriptor terminalFileDescriptorWrapped = wrapFileDescriptor(mTerminalFileDescriptor); *** new Thread(&quot;TermSessionOutputWriter[pid=&quot; + mShellPid + &quot;]&quot;) &#123; @Override public void run() &#123; final byte[] buffer = new byte[4096]; try (FileOutputStream termOut = new FileOutputStream(terminalFileDescriptorWrapped)) &#123; while (true) &#123; int bytesToWrite = mTerminalToProcessIOQueue.read(buffer, true); if (bytesToWrite == -1) return; termOut.write(buffer, 0, bytesToWrite); &#125; &#125; catch (IOException e) &#123; // Ignore. &#125; &#125; &#125;.start(); *** &#125; JNI是native方法的封装类，对应我们之前看到的libtermux.so，看样子它最终是用C来实现的，不过对于安卓java与C没太大差别。我们换到jni，看看termux.c 123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869static int create_subprocess(JNIEnv* env, char const* cmd, char const* cwd, char* const argv[], char** envp, int* pProcessId, jint rows, jint columns)&#123; int ptm = open(&quot;/dev/ptmx&quot;, O_RDWR | O_CLOEXEC); if (ptm &lt; 0) return throw_runtime_exception(env, &quot;Cannot open /dev/ptmx&quot;);#ifdef LACKS_PTSNAME_R char* devname;#else char devname[64];#endif if (grantpt(ptm) || unlockpt(ptm) ||#ifdef LACKS_PTSNAME_R (devname = ptsname(ptm)) == NULL#else ptsname_r(ptm, devname, sizeof(devname))#endif ) &#123; return throw_runtime_exception(env, &quot;Cannot grantpt()/unlockpt()/ptsname_r() on /dev/ptmx&quot;); &#125; // Enable UTF-8 mode and disable flow control to prevent Ctrl+S from locking up the display. struct termios tios; tcgetattr(ptm, &amp;tios); tios.c_iflag |= IUTF8; tios.c_iflag &amp;= ~(IXON | IXOFF); tcsetattr(ptm, TCSANOW, &amp;tios); /** Set initial winsize. */ struct winsize sz = &#123; .ws_row = (unsigned short) rows, .ws_col = (unsigned short) columns &#125;; ioctl(ptm, TIOCSWINSZ, &amp;sz); pid_t pid = fork(); if (pid &lt; 0) &#123; return throw_runtime_exception(env, &quot;Fork failed&quot;); &#125; else if (pid &gt; 0) &#123; *pProcessId = (int) pid; return ptm; &#125; else &#123; *** &#125;&#125;JNIEXPORT jint JNICALL Java_com_termux_terminal_JNI_createSubprocess( JNIEnv* env, jclass TERMUX_UNUSED(clazz), jstring cmd, jstring cwd, jobjectArray args, jobjectArray envVars, jintArray processIdArray, jint rows, jint columns)&#123; *** int procId = 0; char const* cmd_cwd = (*env)-&gt;GetStringUTFChars(env, cwd, NULL); char const* cmd_utf8 = (*env)-&gt;GetStringUTFChars(env, cmd, NULL); int ptm = create_subprocess(env, cmd_utf8, cmd_cwd, argv, envp, &amp;procId, rows, columns); (*env)-&gt;ReleaseStringUTFChars(env, cmd, cmd_utf8); (*env)-&gt;ReleaseStringUTFChars(env, cmd, cmd_cwd); *** return ptm;&#125; 当我看到int ptm = open(&quot;/dev/ptmx&quot;, O_RDWR | O_CLOEXEC);的时候，算了，我们实现的方式看来存在根本上的区别，我就说为什么每次运行Termux的时候通知栏有一个消不掉的东西(手机截不到屏)，这原来是系统的东西，我还以为是程序的东西。欸欸，有没有觉得很惊讶，是我孤陋寡闻了？嘿嘿，看我为什么做了这么多铺垫，意义不就来了嘛！至于“/dev/ptmx”嘛，它是linux内核自带的伪终端设备，看来android果然天生就是linux，好了，我们说过就是速览一下，看来也该结束了，你问我怎么实现的好像没说？我只能说补一补linux的知识吧。
